import React from 'react';
import $ from 'jquery';
import '../Style/Sidebar.css';

class Sidebar extends React.Component {

    componentDidMount = () => {
         $(document).ready(function () {
             $('.nav-a').hover(function () {
                 $('.arrow-see').addClass('angle right icon');
             })
         })
    }


    render(){
        return (
                <div className= "sidebar">
                    <div className= "sidebar-inner">
                        <a href = "home"><img src = {require('../Images/portrait.jpg')} alt = "My Image" class = "my-image"/></a>
                        <a href = "home"><h2 className= "name">NAYAN GOSWAMI</h2></a>
                        <a href = "home"><h4 className= "developer-title">FULL-STACK WEB DEVELOPER</h4></a>

                        <div className= "navigation-panel">
                            <ul className = "nav-list">
                                <li className= "nav-links"><a className= "nav-a" href = "#">PORTFOLIO<i className="arrow-see"></i></a></li>
                                <li className= "nav-links"><a className= "nav-a" href = "#">SKILLS<i></i></a></li>
                                <li className= "nav-links"></li>
                                <li className= "nav-links"><a className= "nav-a" href = "#">CONTACT ME<i></i></a></li>
                                <li className= "nav-links"><a className= "nav-a" href = "#">MY CV<i className="angle right icon"></i></a></li>
                            </ul>
                        </div>

                        <div className = "icon-panel">
                            <p className= "icon-title">Get in touch</p>
                                <ul className= "icon-ul">
                                    <li className= "icon-li"><a className= "icons-a" href = "#"><i className="envelope open icon main-icons"></i></a></li>
                                    <li className= "icon-li"><a className= "icons-a" href = "#"><i className="linkedin icon main-icons"></i></a></li>
                                    <li className= "icon-li"><a className= "icons-a" href = "#"><i className="github square icon main-icons"></i></a></li>
                                </ul>
                            </div>
                        </div>
                    </div>
        );
    }
}

export default Sidebar;
