import React from 'react';
import $ from 'jquery';
import '../Style/Sidebar.css';

class Sidebar extends React.Component {

    componentDidMount = () => {

    }


    render(){
        return (
                <div className= "sidebar">
                    <div className= "sidebar-inner">
                     <img src = {require('../Images/portrait.jpg')} alt = "My Image" class = "my-image"/>
                     <h2 className= "name">NAYAN GOSWAMI</h2>
                     <h4 className= "developer-title">FULL-STACK WEB DEVELOPER</h4>

                        <div className= "navigation-panel">
                            <ul className = "nav-list">
                                <li className= "nav-links"><a className= "nav-a" href = "#">PORTFOLIO</a></li>
                                <li className= "nav-links"><a className= "nav-a" href = "#">SKILLS</a></li>
                                <li className= "nav-links"><i className="fab fa-linkedin"></i></li>
                                <li className= "nav-links"><a className= "nav-a" href = "#">CONTACT ME</a></li>
                                <li className= "nav-links"><a className= "nav-a" href = "#">MY CV</a></li>
                            </ul>
                        </div>

                        <div className = "icon-panel">
                            <div className= "row">
                                <div className= "col-md-4">
                                   <i className=" fas fa-envelope-open"></i>
                                </div>
                                <div className= "col-md-4">
                                    <i className="fab fa-linkedin"></i>
                                </div>
                                <div className= "col-md-4">
                                    <i className=" fas fa-envelope-open"></i>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
        );
    }
}

export default Sidebar;
