                $('.arrow-see4').addClass('angle right icon');
            })

            $('.nav-a4').mouseout(function () {
                $('.arrow-see4').removeClass('angle right icon');
            })

        })
    }


    render(){
        return (
            <div className= "sidebar">
               
            </div>
        );
    }
}

export default Sidebar;
